import { ErrorMessage, Field, Form, Formik } from "formik";
import Button from "../utils/Button";
import * as yup from 'yup';



export default function CreateGenres() {


    return (
        <>
            <h3>Create Genres Test</h3>


            <Formik initialValues={{
                name: ''
            }}
                onSubmit={value => {
                    //when the form is posted
                    console.log(value);
                }}
                validationSchema={yup.object({
                    name: yup.string().required("This field is required").max(30)
                })
                }
            >
                <Form>
                    <div className="mb-3">
                        <label htmlFor="name">Name</label>
                        <Field name="name" id="name" className="form-control" />
                        <ErrorMessage name="name">{msg => <div className="text-danger">{msg}</div>}</ErrorMessage>
                            
                    </div>

                    <Button type="submit">Save Chagens</Button>
                    <a className="btn btn-secondary" href="/genres">Cancel</a>

                </Form>

            </Formik>
        </>
    )
}
