import { Form, Formik } from "formik";
import IGenreDetals from "../Genres/Genres.model";

export default function FilterMovie() {

    const initialValues: FilterMovieProps = {
        title: '',
        genreId: 0,
        upcomingReleases: false,
        inTheaters: false
    }

    const genres: IGenreDetals[] = [{ id: 1, name: 'Action' }, { id: 2, name: 'Comedy' }, { id: 3, name: 'Thriller' }]


    return (
        <>
            <h3>Filter Movie</h3>
            <Formik initialValues={initialValues}
                onSubmit={value => console.log(value)}
            >
                {(formikProps) => (
                    <Form>
                        <div className="row gx-3 align-items-content">
                            <div className="col-auto">
                                <input type="text" className="form-control" id="title" placeholder="Title of the Movie"
                                    {...formikProps.getFieldProps('title')} />
                            </div>

                            <div className="col-auto">
                                <select className="form-select" {...formikProps.getFieldProps('genreId')}>
                                    <option value="0">Select Genre</option>
                                    <option value="1">Action</option>
                                    <option value="2">Comedy</option>
                                    <option value="3">Thriller</option>
                                    {genres.map(genre => <option key={genre.id} value={genre.id}>{genre.name}</option>)}
                                </select>
                            </div>

                            <div className="col-auto">
                                <div className="form-check">
                                    <input type="checkbox" className="form-check-input" id="upcomingReleases"
                                        {...formikProps.getFieldProps('upcomingReleases')} />
                                    <label className="form-check-label" htmlFor="upcomingReleases">Upcoming Releases</label>
                                </div>

                            </div>
                        </div>
                    </Form>
                )}


            </Formik>

        </>
    )
}

interface FilterMovieProps {
    title: string;
    genreId: number;
    upcomingReleases: boolean;
    inTheaters: boolean;
}
