import React, { useState, useEffect } from "react";
import Table from "../components/Table";
import * as apiEndpoints from "../apiEndpoints/ProjectEndpoints"; 

// Define the Project interface
interface Project {
    projectId: number;
    name: string;
    deadline: string; // Or Date, depending on how you manage dates
    // Add any other properties that a Project might have
}

export default function Projects() {
    const [projects, setProjects] = useState<Project[]>([]);
    const [searchTerm, setSearchTerm] = useState("");

    useEffect(() => {
        // Fetch projects from the API using the imported function
        fetchProjects();
    }, []);

    const fetchProjects = () => {
        apiEndpoints.getAll()
            .then(data => setProjects(data))
            .catch(error => console.error('Error fetching projects:', error));
    };

    const handleAddProject = () => {
        // Implement add project logic (e.g., show a modal or a form)
    };

    const handleSearch = (e: React.FormEvent<HTMLFormElement>) => {
        e.preventDefault();
        // Implement search logic
        // For now, let's just re-fetch all projects without filtering
        fetchProjects();
    };

    const handleSearchChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        setSearchTerm(e.target.value);
    };

    return (
        <>
            <div className="flex items-center my-6">
                <div className="w-1/2">
                    <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded" onClick={handleAddProject}>
                        Add entry
                    </button>
                </div>

                <div className="w-1/2 flex justify-end">
                    <form onSubmit={handleSearch}>
                        <input
                            className="border rounded-full py-2 px-4"
                            type="search"
                            placeholder="Search"
                            aria-label="Search"
                            value={searchTerm}
                            onChange={handleSearchChange}
                        />
                        <button
                            className="bg-blue-500 hover:bg-blue-700 text-white rounded-full py-2 px-4 ml-2"
                            type="submit"
                        >
                            Search
                        </button>
                    </form>
                </div>
            </div>
            <Table projects={projects} />
        </>
    );
}
