import React, { useEffect, useState } from "react";
import * as projectEndpoints from "../../endpoints/ProjectEndpoints";

interface CreateProjectProps {
    onProjectCreated: () => void;
}

const initialProjectState = {
    name: '',
    description: '',
    deadline: '',
    isCompletedStatus: false,
};

export default function CreateProject({ onProjectCreated }: CreateProjectProps) {
    const [project, setProject] = useState(initialProjectState);
    const [autoCompleted, setAutoCompleted] = useState(false);

    useEffect(() => {
        const today = new Date().toISOString().split('T')[0];
        
        if (project.deadline == today && !autoCompleted) {
            setProject(prevProject => ({
                ...prevProject,
                isCompletedStatus: true,
            }));
            setAutoCompleted(true);
        }
    }, [project.deadline, autoCompleted]);

    useEffect(() => {
        const todayStr = new Date().toISOString().split('T')[0];
        const deadlineDate = new Date(project.deadline);
        // Convert the deadline to 'YYYY-MM-DD' format for a direct comparison
        const formattedDeadline = deadlineDate.toISOString().split('T')[0];

        // If the deadline is today or in the past, mark as completed
        if (formattedDeadline <= todayStr) {
            setProject(prevProject => ({
                ...prevProject,
                isCompletedStatus: true,
            }));
        }
    }, [project.deadline]);


    const handleChange = (e: { target: { name: any; value: any; type: any; checked: any; }; }) => {
        const { name, value, type, checked } = e.target;
        setProject(prevProject => ({
            ...prevProject,
            [name]: type === 'checkbox' ? checked : value,
        }));
    };

    const handleSubmit = async (e: { preventDefault: () => void; }) => {
        e.preventDefault();

        try {
            await projectEndpoints.createProject(project);
            onProjectCreated();
            setProject(initialProjectState);
        } catch (error) {
            console.error("Failed to create project", error);
        }
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name"
                value={project.name}
                onChange={handleChange}
                placeholder="Project Name"
            />
            <input
                type="text"
                name="description"
                value={project.description}
                onChange={handleChange}
                placeholder="Description"
            />
            <input
                type="date"
                name="deadline"
                value={project.deadline}
                onChange={handleChange}
                placeholder="Deadline"
            />
            <label>
                Completed:
                <input
                    type="checkbox"
                    name="isCompletedStatus" // Ensure the 'name' matches the state property
                    checked={project.isCompletedStatus}
                    onChange={handleChange} />
            </label>
            <button type="submit">Create Project</button>
        </form>
    );
}
