import React, { useState } from 'react';


const TimeLogger: React.FC<TimeLoggerProps> = ({ selectedProject, onLogTime }) => {
  const [startTime, setStartTime] = useState('');
  const [endTime, setEndTime] = useState('');

  const handleSubmit = (e: React.FormEvent<HTMLFormElement>) => {
    e.preventDefault();
    onLogTime({ projectId: selectedProject.projectId, startTime, endTime });
  };

  return (
    <form onSubmit={handleSubmit}>
      <h2>Log Time for: {selectedProject?.name}</h2>
      <label>
        Start Time:
        <input type="datetime-local" value={startTime} onChange={e => setStartTime(e.target.value)} />
      </label>
      <label>
        End Time:
        <input type="datetime-local" value={endTime} onChange={e => setEndTime(e.target.value)} />
      </label>
      <button type="submit">Log Time</button>
    </form>
  );
};

interface Project {
    projectId: number;
    name: string;
    deadline: string; // or Date, depending on how you manage dates
  }
  
  interface TimeLoggerProps {
    selectedProject: Project;
    onLogTime: (log: { projectId: number; startTime: string; endTime: string }) => void;
  }

export default TimeLogger;
