import axios from 'axios';

const BASE_URL = process.env.REACT_APP_API_URL;

interface Project {
    projectId: number;
    name: string;
    deadline: string; // or Date, depending on how you manage dates
}

export async function getAll() {
    try {
        const response = await axios.get(`${BASE_URL}/projects`);
        console.log('Fetched projects:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error fetching projects:', error);
        throw error;
    }
}

export async function getById(id: number) {
    try {
        const response = await axios.get(`${BASE_URL}/projects/${id}`);
        console.log(`Fetched project with ID ${id}:`, response.data);
        return response.data;
    } catch (error) {
        console.error(`Error fetching project with ID ${id}:`, error);
        throw error;
    }
}

export async function createProject(project: Project) {
    try {
        const response = await axios.post(`${BASE_URL}/projects`, project);
        console.log('Created project:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error creating project:', error);
        throw error;
    }
}

export async function updateProject(project: Project) {
    try {
        const response = await axios.put(`${BASE_URL}/projects/${project.projectId}`, project);
        console.log('Updated project:', response.data);
        return response.data;
    } catch (error) {
        console.error('Error updating project:', error);
        throw error;
    }
}

export async function removeProject(id: number) {
    try {
        const response = await axios.delete(`${BASE_URL}/projects/${id}`);
        console.log(`Deleted project with ID ${id}:`, response.data);
        return response.data;
    } catch (error) {
        console.error(`Error deleting project with ID ${id}:`, error);
        throw error;
    }
}
