import { useEffect, useState } from "react";
import React from "react";
import * as projectEndpoints from "../../endpoints/ProjectEndpoints";

interface CreateProjectProps {
    onProjectCreated: () => void;
}

const initialProjectState = {
    id: undefined,
    name: '',
    description: '',
    deadline: '', 
    isCompletedStatus: false
};

export default function CreateProject({ onProjectCreated }: CreateProjectProps) {
    // const [project, setProject] = useState<ProjectCreationDto>();

    const [project, setProject] = useState(initialProjectState);
    const [autoCompleted, setAutoCompleted] = useState(false);

    useEffect(() => {
        const today = new Date().toISOString().split('T')[0];
        if (project.deadline === today && !autoCompleted) {
          setProject(prevProject => ({
            ...prevProject,
            isCompleted: true,
          }));
          setAutoCompleted(true); // Indicate that the completion was automatically set
        } else if (project.deadline !== today && autoCompleted) {
          // Optional: Automatically unset isCompleted if the deadline changes away from today
          setAutoCompleted(false);
        }
        // If you want to allow users to manually change `isCompleted` even after it was auto-set,
        // you may not need to automatically unset it based on deadline changes.
      }, [project.deadline, autoCompleted]);

    const handleChange = (e: { target: { name: any; value: any; type: any; checked: any; }; }) => {
        const { name, value, type, checked } = e.target;
        setProject(prevProject => ({
          ...prevProject,
          [name]: type === 'checkbox' ? checked : value,
        }));
      };

      

    const handleSubmit = (e: { preventDefault: () => void; }) => {
        e.preventDefault();

        if (project) {
            projectEndpoints.createProject(project).then(() => {
                onProjectCreated(); // Notify parent component
            }).catch(error => {
                console.error("Failed to create project", error);
            });
        } else {
            console.error("Project data is undefined.");
        }
        setProject(project);
    };

    return (
        <form onSubmit={handleSubmit}>
            <input
                type="text"
                name="name" // Use the 'name' attribute for identifying the input
                value={project.name}
                onChange={handleChange}
                placeholder="Project Name"
            />
            <input
                type="text"
                name="description"
                value={project.description}
                onChange={handleChange}
                placeholder="Description"
            />
            <input
                type="date"
                name="deadline"
                value={project.deadline}
                onChange={handleChange}
                placeholder="Deadline"
            />
            <label>
                Completed:
                <input
                    type="checkbox"
                    name="isCompleted"
                    checked={project.isCompletedStatus}
                    onChange={handleChange} />
            </label>
            <button type="submit">Create Project</button>
        </form>
    );
}
